// n8n Function Node Code - Complete Great Harvest Crown Point Analytics Email
// Corporate Colors: Main #0c2340, Highlight #d84127

// Get all query results from previous Postgres nodes
const summaryData = $node["Query1_Summary"].json[0];
const keywordsPositive = $node["Query2_Keywords_Positive"].json;
const keywordsNegative = $node["Query3_Keywords_Negative"].json;
const locationData = $node["Query4_Locations"].json;
const negativeData = $node["Query5_Negative"].json;
const teamData = $node["Query6_Team"].json;
const trendData = $node["Query7_Trends"].json;
const executiveData = $node["Query8_Executive"].json[0];
const hourlyData = $node["Query10_Hourly"].json;
const weeklyData = $node["Query11_Weekly"].json;
const dayOfWeekData = $node["Query12_DayOfWeek"].json;

// Helper functions
const formatNumber = (value) => parseInt(value || 0).toLocaleString('en-US');
const formatPercent = (value) => parseFloat(value || 0).toFixed(1) + '%';
const formatRating = (value) => parseFloat(value || 0).toFixed(1);
const formatCurrency = (value) => '$' + parseFloat(value || 0).toFixed(2);

// Generate star rating display
const starRating = (rating) => {
  const full = '‚òÖ';
  const empty = '‚òÜ';
  const ratingNum = parseFloat(rating || 0);
  let stars = '';
  for (let i = 1; i <= 5; i++) {
    stars += i <= ratingNum ? full : empty;
  }
  return stars;
};

// Generate visual rating bar with percentage
const ratingBar = (rating, showLabel = true) => {
  const percentage = (rating / 5) * 100;
  const color = rating >= 4 ? '#27ae60' : rating >= 3 ? '#f39c12' : '#d84127';
  return `
    <div style="display: flex; align-items: center; gap: 10px;">
      <div style="background: #e0e0e0; height: 10px; border-radius: 5px; flex: 1; position: relative;">
        <div style="background: ${color}; width: ${percentage}%; height: 100%; border-radius: 5px;"></div>
      </div>
      ${showLabel ? `<span style="font-size: 14px; font-weight: bold; min-width: 35px;">${formatRating(rating)}</span>` : ''}
    </div>
  `;
};

// Generate trend indicator
const trendIndicator = (current, previous) => {
  if (!previous || !current) return '';
  const diff = current - previous;
  const percent = ((diff / previous) * 100).toFixed(1);
  if (Math.abs(diff) < 0.01) return '<span style="color: #666;">‚Üí 0%</span>';
  return diff > 0 
    ? `<span style="color: #27ae60;">‚Üë ${percent}%</span>`
    : `<span style="color: #d84127;">‚Üì ${percent}%</span>`;
};

// Generate keyword cloud HTML
const keywordCloud = (keywords, color) => {
  if (!keywords || keywords.length === 0) return '<p style="color: #666;">No significant keywords found</p>';

  return keywords.slice(0, 15).map(kw => {
    const size = Math.max(14, Math.min(24, 14 + (kw.frequency * 2)));
    const opacity = Math.max(0.6, Math.min(1, 0.6 + (kw.frequency * 0.05)));
    return `<span style="
      display: inline-block;
      padding: 4px 8px;
      margin: 4px;
      font-size: ${size}px;
      color: ${color};
      opacity: ${opacity};
      border: 1px solid ${color};
      border-radius: 4px;
    ">${kw.keyword} (${kw.frequency})</span>`;
  }).join('');
};

// Generate hourly heat map
const hourlyHeatMap = (data) => {
  if (!data || data.length === 0) return '';

  const maxSurveys = Math.max(...data.map(h => h.survey_count));

  return data.map(hour => {
    const intensity = hour.survey_count / maxSurveys;
    const bgColor = hour.avg_rating >= 4 ? `rgba(39, 174, 96, ${intensity})` : 
                    hour.avg_rating >= 3 ? `rgba(243, 156, 18, ${intensity})` :
                    `rgba(216, 65, 39, ${intensity})`;

    return `
      <div style="
        display: inline-block;
        width: 40px;
        height: 40px;
        margin: 2px;
        background: ${bgColor};
        border: 1px solid #ddd;
        border-radius: 4px;
        text-align: center;
        line-height: 40px;
        font-size: 11px;
        font-weight: bold;
        position: relative;
        cursor: default;
      " title="${hour.time_of_day}: ${hour.survey_count} surveys, ${formatRating(hour.avg_rating)}‚òÖ">
        ${hour.time_of_day.replace(/ (AM|PM)/, '')}
      </div>
    `;
  }).join('');
};

// Check for alerts based on dynamic thresholds
const alerts = [];
const criticalRating = 3.8; // These would come from your threshold query
const warningNegativeDaily = 5;
const criticalNegativeDaily = 10;
const minResponseRate = 80;

if (summaryData.avg_rating_today && summaryData.avg_rating_today < criticalRating) {
  alerts.push({
    type: 'critical',
    icon: 'üö®',
    message: `Today's average rating (${formatRating(summaryData.avg_rating_today)}) is below critical threshold (${criticalRating})`
  });
}

if (summaryData.negative_today > criticalNegativeDaily) {
  alerts.push({
    type: 'critical',
    icon: '‚ö†Ô∏è',
    message: `High volume of negative feedback today: ${summaryData.negative_today} negative reviews`
  });
}

const responseRate = parseFloat(summaryData.negative_response_rate || 100);
if (responseRate < minResponseRate) {
  alerts.push({
    type: 'warning',
    icon: 'üì¢',
    message: `Response rate to negative feedback (${formatPercent(responseRate)}) is below target (${minResponseRate}%)`
  });
}

// Count unresponded negative feedback from last 48 hours
const unrespondedCount = negativeData.filter(f => !f.has_response && f.days_ago <= 2).length;
if (unrespondedCount > 0) {
  alerts.push({
    type: 'warning',
    icon: 'üí¨',
    message: `${unrespondedCount} negative feedback items need responses (< 48 hours old)`
  });
}

// Generate alert section HTML
let alertSection = '';
if (alerts.length > 0) {
  const criticalAlerts = alerts.filter(a => a.type === 'critical');
  const warningAlerts = alerts.filter(a => a.type === 'warning');

  alertSection = `
    <div style="padding: 20px;">
      ${criticalAlerts.length > 0 ? `
        <div style="background-color: #d84127; color: white; padding: 15px; margin-bottom: 15px; border-radius: 8px;">
          <h3 style="margin: 0 0 10px 0; font-size: 18px;">üö® CRITICAL ALERTS</h3>
          ${criticalAlerts.map(a => `<p style="margin: 5px 0; font-size: 14px;">${a.icon} ${a.message}</p>`).join('')}
        </div>
      ` : ''}
      ${warningAlerts.length > 0 ? `
        <div style="background-color: #f39c12; color: white; padding: 15px; border-radius: 8px;">
          <h3 style="margin: 0 0 10px 0; font-size: 18px;">‚ö†Ô∏è WARNINGS</h3>
          ${warningAlerts.map(a => `<p style="margin: 5px 0; font-size: 14px;">${a.icon} ${a.message}</p>`).join('')}
        </div>
      ` : ''}
    </div>
  `;
}

// Generate location performance rows
let locationTableHTML = '';
locationData.forEach((loc, index) => {
  const ratingColor = loc.avg_rating >= 4 ? '#27ae60' : loc.avg_rating >= 3.5 ? '#f39c12' : '#d84127';
  const rowBg = index % 2 === 0 ? '#ffffff' : '#f8f9fa';

  locationTableHTML += `
    <tr style="background: ${rowBg};">
      <td style="padding: 12px; border-bottom: 1px solid #e0e0e0;">
