  const timeColor = avgTime <= 60 ? '#27ae60' : avgTime <= 120 ? '#f39c12' : '#d84127';
  const rowBg = index % 2 === 0 ? '#ffffff' : '#f8f9fa';

  teamTableHTML += `
    <tr style="background: ${rowBg};">
      <td style="padding: 12px; border-bottom: 1px solid #e0e0e0;">
        <strong>${member.responder}</strong>
      </td>
      <td style="padding: 12px; text-align: center; border-bottom: 1px solid #e0e0e0;">
        ${member.responses_handled}
      </td>
      <td style="padding: 12px; text-align: center; border-bottom: 1px solid #e0e0e0;">
        <span style="color: ${timeColor}; font-weight: bold;">
          ${avgTime} min
        </span>
      </td>
      <td style="padding: 12px; text-align: center; border-bottom: 1px solid #e0e0e0;">
        ${member.responses_under_1hr}
        <span style="color: #666; font-size: 12px;">
